from .models import Profile, Productfrom rest_framework.serializers import ModelSerializerfrom django.contrib.auth.models import Userclass ProfileSerializer(ModelSerializer):    class Meta:        model = Profile        fields = (            '__all__'        )        read_only_fields = ('username',)    def to_representation(self, instance):        response = super().to_representation(instance)        response['username'] = UserSerializer(instance.username).data        return responseclass UserSerializer(ModelSerializer):    class Meta:        model = User        fields = (            'id', 'username', 'password',            'email', 'is_staff', 'is_active',            'is_superuser', 'last_login', 'date_joined'        )class ProductSerializer(ModelSerializer):    class Meta:        model = Product        fields = (            '__all__'        )